import React, { useEffect, useState } from "react";
import Logout from "../components/Log/logout";
import Logo from "../components/Logo";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css'

const Post = () => {
    const [data, setData] = useState([]);
    // Le useEffect se joue lorsque le composant Post est monté
    useEffect(() => {
        axios({
            method: "get",
            url: `${process.env.REACT_APP_API_URL}api/post`,
            // withCredentials: true,
            // data: {
            //     email,
            //     password,
            // },  
        }).then((res) => setData(res.data));
    }, []);

    return (

        <div className="welcome">
            <Logo />
            <h1> Liste des demandes de service </h1> <br />

            <table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Nom et Prenom</th>
      <th scope="col">Service</th>
      <th scope="col">Adresse</th>
      <th scope="col">Contact</th>
    </tr>
  </thead>
  <tbody>
    <tr>
    {data.map((post) => (
                    <td>{post.name}</td>
                ))}
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td colspan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>

{/* 
            <table>
                <th>Nom et Prenom<tr>{data.map((post) => (
                    <li>{post.name}</li>
                ))}</tr></th>
                <th>service <tr> {data.map((post) => (
                    <li>{post.service}</li>
                ))}</tr></th>
                <th>Adresse <tr> {data.map((post) => (
                    <li>{post.adresse}</li>
                ))}</tr></th>
                <th>Phone <tr>{data.map((post) => (
                    <li>{post.phone}</li>
                ))} </tr></th>
            </table> */}



        </div>
    );
};

export default Post;
