import React, { useState } from 'react';
import Logo from '../components/Logo';
import axios from 'axios';

const Demande = () => {

    const [name, setName] = useState('');
    const [service, setService] = useState('');
    const [adresse, setAdresse] = useState('');
    const [phone, setPhone] = useState('');

    axios({
        method: "post",
        url: `${process.env.REACT_APP_API_URL}api/post`,
        // withCredentials: true,
        data: {
            name,
            service,
            adresse,
            phone,
        },
    })
        .then((res) => {
            console.log(res);
        }).catch((err) => {
            console.log(err);
        });




    return (
        <div className='agence-page'>
            <Logo /> <br />
            <h1> Lancez votre demande </h1> <br />

            <div className="connection-form">

                    <form action="" onSubmit={Demande} id="sign-up-form" >
                        <label htmlFor="name">Prenom et Nom</label>
                        <br /> <br />
                        <input
                            type="text"
                            name='name'
                            id='name'
                            onChange={(e) => setName(e.target.value)}
                            value={name} className="champ" />
                        <br />
                        <label htmlFor="service"> Service </label>
                        <br />
                        <input
                            type="text"
                            name='service'
                            id='service'
                            onChange={(e) => setService(e.target.value)}
                            value={service} className="champ" />
                        <br />
                        <label htmlFor="adresse">Adresse</label>
                        <br />
                        <input
                            type="text"
                            name='adresse'
                            id='adresse'
                            onChange={(e) => setAdresse(e.target.value)}
                            value={adresse} className="champ" />
                        <br />
                        <label htmlFor="phone">Numero telephone</label>
                        <br />
                        <input
                            type="number"
                            id='phone'
                            onChange={(e) => setPhone(e.target.value)}
                            value={phone} className="champ" />
                        <br />
                        <input type="submit" value="Envoyer" id='sub' />
                    </form>
            </div>
        </div>
    );
};

export default Demande;
